include.java:
public class HttpResponseCodeAspect implements IURAspect
{
	private int mCode;
	private String mMessage;

	/** Creates a new instance of HttpClientConfigAspect */
	public HttpResponseCodeAspect(int aCode, String aMessage)
	{	mCode=aCode; mMessage=aMessage;
	}

	public int getCode()
	{	return mCode;
	}

	public String getMessage()
	{	return mMessage;		
	}
}
